import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import {dataFake} from '../../data/dataFake'

@Component({
  selector: 'app-content',
  templateUrl: './content.component.html',
  styleUrls: ['./content.component.css']
})
export class ContentComponent implements OnInit {
  photoCover:string = "http://localhost:4200/assets/angular.jpeg"
  contentTitle:string = "MINHA NOTICIA DE EXEMPLO"
  contentDescription:string = "BLA BLA BLA BLA BLA"
  private id:string | null = "0"

  constructor(
    private route:ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe( value =>
      this.id = value.get("id")
    )
    this.setValuesToComponent()
  }

  setValuesToComponent(id:string) {
    const result = dataFake.filter(article => article.id == id)
   console.log(result)
  }


}
<div class="container__content">

    <div class="content__cover">
        <img src="{{ photoCover }}" alt="" srcset="">
    </div>
    <div class="content__title">
        <h1>{{ contentTitle }}</h1>
        <hr/>

    </div>
    <div class="content_descripition">
        <a [routerLink]="['']"> << Voltar </a>
        <p>{{ contentDescription }}</p>
    </div>
</div>
